// import { ADD_DISH } from './actions'


const initialState = {
    data: [],
    recepts: []
}

export const dishReducer = (state = initialState, action) => {
    switch (action.type) {
        // case GET_DISH: {
        //     const data = action.payload
        //     return [...state, data]
        // }
        case 'GET_DISH_SUCCESSFULL':
            const item = action.payload.data
            return {
                data: item,
                recepts: state.recepts
            }
        case 'ADD_DISH':
            const dataDish = action.payload.data
            return {
                data: state.data,
                recepts: [state.recepts, dataDish]
            }


        default:
            return state;
    }
}
