import React, { useState } from 'react';
import Button from '@mui/material/Button';
import Stack from '@mui/material/Stack';
import Dish from './dishIItem';
import Favourites from './favourites';
import Backdrop from '@mui/material/Backdrop';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import { useDispatch } from "react-redux";
import CancelPresentationTwoToneIcon from '@mui/icons-material/CancelPresentationTwoTone';



function TopBar() {
    let displ = null;
    const [activeTab, setactiveTab] = useState('randomDish');
    const [open, setOpen] = useState(false);
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const dispatch = useDispatch();
    const strMeal = document.getElementById('margin-none');
    const strInstructions = document.getElementById('margin-dense');


    if (activeTab === 'favourites') {
        displ = <Favourites />;
    }
    if (activeTab === 'randomDish') {
        displ = <Dish />;
    }

    const addDish = () => {
        setOpen(false);
        dispatch({
            type: 'ADD_DISH',
            payload: {
                data: [{
                    strMeal: strMeal.value,
                    strInstructions: strInstructions.value,
                    strMealThumb: 'https://res.cloudinary.com/crunchbase-production/image/upload/c_lpad,f_auto,q_auto:eco,dpr_1/v1479829522/bxpgav8mvdsibvxctd1d.jpg'
                }]
            }
        })
        setTitle('');
        setDescription('');
    }

    const handleToggle = () => {
        setOpen(!open);
    };
    const addTitleDish = (event) => {
        setTitle(event.target.value);
    };
    const addDescriptionDish = (event) => {
        setDescription(event.target.value);
    };

    return (
        <div className='container'>
            <div className="topBar">
                <Stack direction="row" spacing={2}>
                    <Button color='white' id='one' variant="outlined" onClick={() => setactiveTab('randomDish')} > Random dish </Button>
                    <Button id='one' variant="outlined" onClick={() => setactiveTab('favourites')} > Favourites </Button>
                    <Button id='one' variant="outlined" onClick={handleToggle} > Add custom dish </Button>
                    <Backdrop
                        sx={{ color: '#fff', zIndex: (theme) => theme.zIndex.drawer + 1 }}
                        open={open}

                    >
                        <Box
                            sx={{
                                display: 'flex',
                                flexDirection: 'column',
                                '& .MuiTextField-root': { width: '45ch' },
                                margin: '30px',
                            }}
                        >
                            <Card>
                                <CardContent>
                                    <div className="close" >
                                        <CancelPresentationTwoToneIcon
                                            fontSize="medium"
                                            onClick={() => setOpen(!open)}
                                        />
                                    </div>
                                    <CardMedia
                                        component="img"
                                        height="300"
                                        image='https://res.cloudinary.com/crunchbase-production/image/upload/c_lpad,f_auto,q_auto:eco,dpr_1/v1479829522/bxpgav8mvdsibvxctd1d.jpg'
                                        alt="" />
                                    <TextField
                                        value={title}
                                        label={'Dish title'}
                                        id="margin-none" multiline
                                        onChange={addTitleDish} />
                                    <br />
                                    <br />
                                    <br />
                                    <TextField
                                        value={description}
                                        label={'Dish description...'}
                                        id="margin-dense" multiline
                                        onChange={addDescriptionDish} />
                                </CardContent>
                                <Stack margin={'20px'}>
                                    <Button variant="contained" onClick={addDish} > Add Custom Dish </Button>
                                </Stack>
                            </Card >
                        </Box >

                    </Backdrop>
                </Stack>
            </div >
            <div>
                {displ}
            </div>
        </div>
    )
}


export default TopBar;
