import React, { useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Typography from '@mui/material/Typography';
import ShowRecept from './showRecept';



function Favourites() {
    // let recepts
    const receptsData = useSelector(state => state.dish.recepts)
    console.log(receptsData);

    // const [show, setShow] = useState(recepts)

    const dispatch = useDispatch();

    dispatch(() => {
        console.log('ok');
    }, [recepts])

    let recepts = receptsData.map(el => (
        <div className="dish" >
            <Card sx={{ maxWidth: 245 }} onClick={() => showRecept(el)}>
                <CardMedia
                    component="img"
                    height="160"
                    image={el[0].strMealThumb}
                    alt="" />
                <CardContent>
                    <Typography gutterBottom variant="h5" component="div">
                        {el[0].strArea} , {el[0].strCategory}
                    </Typography>
                    <Typography variant="body2" color="text.secondary" >
                        {el[0].strMeal}
                    </Typography >
                </CardContent >
            </Card >
        </div>
    ))

    function showRecept(el) {
        console.log(el);
        dispatch({
            type: 'SHOW_RECEPT',
            payload: { data: el }
        })
        recepts = <ShowRecept />;
    }

    return (
        <div className="myDishs">
            {recepts}
        </div>
    )
}


export default Favourites;
