// import { ADD_DISH } from './actions'


const initialState = {
    data: [],
    recepts: []
}

export const dishReducer = (state = initialState, action) => {
    switch (action.type) {
        // case GET_DISH: {
        //     const data = action.payload
        //     return [...state, data]
        // }
        case 'GET_DISH_SUCCESSFULL':
            return {
                data: action.payload
            }
        case 'ADD_DISH':
            const data = action.payload.data
            return [...state, data]


        default:
            return state;
    }
}
