import React, { useState } from "react";
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Typography from '@mui/material/Typography';
import { useSelector } from "react-redux";
import Favourites from './favourites';



function ShowRecept() {
    let image
    const receptData = useSelector(state => state.dish.showRecept)
    console.log(receptData);

    const [state, setState] = useState(true)

    function myFavouritRacepts() {
        console.log('tyty');
        setState(!state)
    }

    if (receptData !== undefined) {
        (receptData[0].strMealThumb !== undefined) ? image = receptData[0].strMealThumb :
            image = 'https://res.cloudinary.com/crunchbase-production/image/upload/c_lpad,f_auto,q_auto:eco,dpr_1/v1479829522/bxpgav8mvdsibvxctd1d.jpg'
        let ing = [
            receptData[0].strIngredient1,
            receptData[0].strIngredient2,
            receptData[0].strIngredient3,
            receptData[0].strIngredient4,
            receptData[0].strIngredient5,
            receptData[0].strIngredient6,
            receptData[0].strIngredient7,
            receptData[0].strIngredient8,
            receptData[0].strIngredient9,
            receptData[0].strIngredient10,
            receptData[0].strIngredient11,
            receptData[0].strIngredient12,
            receptData[0].strIngredient13,
            receptData[0].strIngredient14,
            receptData[0].strIngredient15,
            receptData[0].strIngredient16,
            receptData[0].strIngredient17,
            receptData[0].strIngredient18,
            receptData[0].strIngredient19,
            receptData[0].strIngredient20
        ]
        ingridient = ing.map(el => (el.length > 1)).map(el => (el + ', '))
    }


    return (
        <div >
            {state ?
                <div className="showRecept">
                    <Card sx={{ maxWidth: 545 }} onClick={() => myFavouritRacepts()}  >
                        <CardMedia
                            component="img"
                            height="360"
                            image={image}
                            alt="" />
                        <CardContent>
                            <Typography gutterBottom variant="h5" component="div">
                                {receptData[0].strArea} , {receptData[0].strCategory}
                            </Typography>
                            <Typography variant="body2" color="text.secondary" >
                                INSTRUCTIONS:
                                <br />
                                {receptData[0].strInstructions}
                            </Typography >
                            <Typography variant="body2" color="text.secondary" >
                                INGRIDIENTS:
                                <br />
                                {ingridient}
                            </Typography >
                        </CardContent >
                    </Card >
                </div>
                : <Favourites />}
        </div>
    )
}

export default ShowRecept;
