import React, { useState } from 'react';
import Button from '@mui/material/Button';
import Stack from '@mui/material/Stack';
import Dish from './dishIItem';
import CustomDishAdd from './addCustomDish';
import Favourites from './favourites';
import Backdrop from '@mui/material/Backdrop';
import Box from '@mui/material/Box';



function TopBar() {
    let displ = null;
    let mainButtons
    const [activeTab, setactiveTab] = useState('randomDish');
    const [open, setOpen] = useState(false);
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const strMeal = document.getElementById('margin-none');
    const strInstructions = document.getElementById('margin-dense');


    if (activeTab === 'favourites') {
        mainButtons = <div>
            <Stack direction="row" spacing={2}>
                <Button id='one' variant="outlined" onClick={() => setactiveTab('randomDish')} > Random dish </Button>
                <Button id='one' variant="outlined" onClick={() => setactiveTab('favourites')} > Favourites </Button>
                <Button id='one' variant="outlined" onClick={() => setactiveTab('addCustomDish')} > Add custom dish </Button>
            </Stack>
        </div>;
        displ = <Favourites />;
    }
    if (activeTab === 'randomDish') {
        mainButtons = <div>
            <Stack direction="row" spacing={2}>
                <Button id='one' variant="outlined" onClick={() => setactiveTab('randomDish')} > Random dish </Button>
                <Button id='one' variant="outlined" onClick={() => setactiveTab('favourites')} > Favourites </Button>
            </Stack>
        </div>;
        displ = <Dish />;
    }
    if (activeTab === 'addCustomDish') {
        mainButtons = null;
        displ =
            <div>
                <Backdrop
                    sx={{ color: '#fff', zIndex: (theme) => theme.zIndex.drawer + 1 }}
                    open={open}
                >
                    <Box
                        sx={{
                            display: 'flex',
                            flexDirection: 'column',
                            '& .MuiTextField-root': { width: '45ch' },
                            margin: '30px',
                        }}
                    >
                        <CustomDishAdd />
                    </Box >

                </Backdrop>;

            </div>

    }

    // const addDish = () => {
    //     setOpen(false);
    //     dispatch({
    //         type: 'ADD_DISH',
    //         payload: {
    //             data: [{
    //                 strMeal: strMeal.value,
    //                 strInstructions: strInstructions.value,
    //                 strMealThumb: 'https://res.cloudinary.com/crunchbase-production/image/upload/c_lpad,f_auto,q_auto:eco,dpr_1/v1479829522/bxpgav8mvdsibvxctd1d.jpg'
    //             }]
    //         }
    //     })
    //     setTitle('');
    //     setDescription('');
    // }

    // const handleToggle = () => {
    //     setOpen(!open);
    // };

    // const addTitleDish = (event) => {
    //     setTitle(event.target.value);
    // };
    // const addDescriptionDish = (event) => {
    //     setDescription(event.target.value);
    // };

    return (
        <div className='container'>
            <div className="topBar">
                {mainButtons}
            </div >
            <div>
                {displ}
            </div>
        </div>
    )
}


export default TopBar;
