import React, { useEffect } from "react"
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import { useDispatch, useSelector } from "react-redux";
import { getData } from '../redux/actions'



function Dish() {
    let image
    let ingridient = []
    const dispatch = useDispatch();
    const data = useSelector(state => state.dish.data)
    const dataDish = data.meals;
    console.log(dataDish);

    useEffect(() => {
        dispatch(getData())
    }, [dispatch])

    if (dataDish !== undefined) {
        (dataDish[0].strMealThumb !== undefined) ? image = dataDish[0].strMealThumb :
            image = 'https://res.cloudinary.com/crunchbase-production/image/upload/c_lpad,f_auto,q_auto:eco,dpr_1/v1479829522/bxpgav8mvdsibvxctd1d.jpg'
        let ing = [
            dataDish[0].strIngredient1,
            dataDish[0].strIngredient2,
            dataDish[0].strIngredient3,
            dataDish[0].strIngredient4,
            dataDish[0].strIngredient5,
            dataDish[0].strIngredient6,
            dataDish[0].strIngredient7,
            dataDish[0].strIngredient8,
            dataDish[0].strIngredient9,
            dataDish[0].strIngredient10,
            dataDish[0].strIngredient11,
            dataDish[0].strIngredient12,
            dataDish[0].strIngredient13,
            dataDish[0].strIngredient14,
            dataDish[0].strIngredient15,
            dataDish[0].strIngredient16,
            dataDish[0].strIngredient17,
            dataDish[0].strIngredient18,
            dataDish[0].strIngredient19,
            dataDish[0].strIngredient20
        ]
        ingridient = ing.map(el => (el.length > 1)).map(el => (el + ', '))
    }

    function ckip() {
        dispatch(
            getData()
        )
    }

    function addDish() {
        dispatch({
            type: 'ADD_DISH',
            payload: { data: dataDish }
        })
    }

    return (
        (dataDish !== undefined) ?
            <div className="dishItems">
                <Card sx={{ maxWidth: 545 }}>
                    <CardMedia
                        component="img"
                        height="360"
                        image={image}
                        alt="" />
                    <CardContent>
                        <Typography gutterBottom variant="h5" component="div">
                            {dataDish[0].strArea} , {dataDish[0].strCategory}
                        </Typography>
                        <Typography>
                            INSTRUCTIONS:
                            <br />
                            {dataDish[0].strInstructions}
                        </Typography>
                    </CardContent >
                    <CardActions>
                        <Button size="small" onClick={ckip}>Skip</Button>
                        <Button size="small" onClick={() => addDish()}>Like </Button>
                    </CardActions>
                </Card >
            </div >
            : <div> ... </div>
    )

}





export default Dish;
