import React, { useState } from 'react';
import Button from '@mui/material/Button';
import Stack from '@mui/material/Stack';
import Dish from './dishIItem';
import Favourites from './favourites';
import AddCustomDish from './addCustomDish';
import Backdrop from '@mui/material/Backdrop';




function TopBar() {
    let displ = null;
    const [activeTab, setactiveTab] = useState('randomDish');
    const [open, setOpen] = useState(false);

    if (activeTab === 'favourites') {
        displ = <Favourites />;
    }
    if (activeTab === 'randomDish') {
        displ = <Dish />;
    }

    const handleClose = () => {
        setOpen(false);
    };
    const handleToggle = () => {
        setOpen(!open);
    };

    return (
        <div className='container'>
            <div className="topBar">
                <Stack direction="row" spacing={2}>
                    <Button variant="outlined" onClick={() => setactiveTab('randomDish')} > Random dish </Button>
                    <Button variant="outlined" onClick={() => setactiveTab('favourites')} > Favourites </Button>
                    <Button variant="outlined" onClick={handleToggle} > Add custom dish </Button>
                    <Backdrop
                        sx={{ color: '#fff', zIndex: (theme) => theme.zIndex.drawer + 1 }}
                        open={open}
                        onClick={handleClose}
                    >
                        <AddCustomDish />
                    </Backdrop>
                </Stack>
            </div >
            <div>
                {displ}
            </div>
        </div>
    )
}


export default TopBar;
