import React, { useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Typography from '@mui/material/Typography';
import ShowRecept from './showRecept';
import Stack from '@mui/material/Stack';
import Button from '@mui/material/Button';



function Favourites() {
    let recepts = <div></div>
    const receptsData = useSelector(state => state.dish.recepts)
    const [myRecepts, setMyRecepts] = useState(false)
    const [open, setOpen] = useState(false);

    const dispatch = useDispatch();

    const handleToggle = () => {
        setOpen(!open);
    };

    myRecepts ? recepts = <ShowRecept /> :
        <div>
            <Stack direction="row" spacing={2}>
                <Button id='one' variant="outlined" onClick={() => setactiveTab('randomDish')} > Random dish </Button>
                <Button id='one' variant="outlined" onClick={() => setactiveTab('favourites')} > Favourites </Button>
                <Button id='one' variant="outlined" onClick={handleToggle} > Add custom dish </Button>
            </Stack>
    recepts = receptsData.map(el => (
            <div className="dish" >
                <Card sx={{ maxWidth: 245 }} onClick={() => showRecept(el)}>
                    <CardMedia
                        component="img"
                        height="160"
                        image={el[0].strMealThumb}
                        alt="" />
                    <CardContent>
                        <Typography variant="body2" color="text.secondary" >
                            {el[0].strMeal}
                        </Typography >
                    </CardContent >
                </Card >
            </div>

            ))
        </div>

    const showRecept = (el) => {
        console.log(el);
        dispatch({
            type: 'SHOW_RECEPT',
            payload: { data: el }
        })
        setMyRecepts(!myRecepts)
    }

    return (
        <div className="myFvoriteDishs">
            {recepts}
        </div>
    )
}


export default Favourites;
