import React, { useState } from 'react';
import Button from '@mui/material/Button';
import Stack from '@mui/material/Stack';
import Dish from './dishIItem';
import Favourites from './favourites';
import Backdrop from '@mui/material/Backdrop';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';


function TopBar() {
    let displ = null;
    const [activeTab, setactiveTab] = useState('randomDish');
    const [open, setOpen] = useState(false);

    if (activeTab === 'favourites') {
        displ = <Favourites />;
    }
    if (activeTab === 'randomDish') {
        displ = <Dish />;
    }

    const handleClose = () => {
        setOpen(false);
        console.log({
            dishTitle: 'qqqqq',

        });
    };

    const handleToggle = () => {
        setOpen(!open);
    };

    return (
        <div className='container'>
            <div className="topBar">
                <Stack direction="row" spacing={2}>
                    <Button variant="outlined" onClick={() => setactiveTab('randomDish')} > Random dish </Button>
                    <Button variant="outlined" onClick={() => setactiveTab('favourites')} > Favourites </Button>
                    <Button variant="outlined" onClick={handleToggle} > Add custom dish </Button>
                    <Backdrop
                        sx={{ color: '#fff', zIndex: (theme) => theme.zIndex.drawer + 1 }}
                        open={open}
                    >
                        <Box
                            sx={{
                                display: 'flex',
                                flexDirection: 'column',
                                '& .MuiTextField-root': { width: '45ch' },
                                margin: '30px',
                            }}
                        >
                            <Card>
                                <CardContent>
                                    <CardMedia
                                        component="img"
                                        height="300"
                                        image='https://res.cloudinary.com/crunchbase-production/image/upload/c_lpad,f_auto,q_auto:eco,dpr_1/v1479829522/bxpgav8mvdsibvxctd1d.jpg'
                                        alt="" />
                                    <TextField label={'Dish title'} id="margin-none" multiline />
                                    <br />
                                    <br />
                                    <br />
                                    <TextField label={'Dish description...'} id="margin-dense" multiline />
                                </CardContent>
                                <Stack margin={'20px'}>
                                    <Button variant="contained" onClick={handleClose} > Add Custom Dish </Button>
                                </Stack>
                            </Card >
                        </Box >

                    </Backdrop>
                </Stack>
            </div >
            <div>
                {displ}
            </div>
        </div>
    )
}


export default TopBar;
