import React, { useState } from 'react';
import Button from '@mui/material/Button';
import Stack from '@mui/material/Stack';
import Dish from './randomDish';
import Favourites from './favourites';
import Backdrop from '@mui/material/Backdrop';
import AddCustomDish from './addCustomDish';
import CloseIcon from '@mui/icons-material/Close';
import IconButton from '@mui/material/IconButton';
import { useDispatch } from "react-redux";




function TopBar() {
    let displ = null;
    let mainButtons
    const dispatch = useDispatch();

    const [activeTab, setactiveTab] = useState('randomDish');
    const [open, setOpen] = useState(false);
    const closeAddCustomDish = () => {
        setOpen(!open);
    };
    const showAddCustomDish = () => {
        setOpen(!open);
    };
    const addDish = () => {
        console.log('add dish');
    }

    if (activeTab === 'randomDish') {
        mainButtons = <div>
            <Stack direction="row" spacing={2}>
                <Button id='one' variant="outlined" onClick={() => setactiveTab('randomDish')} > Random dish </Button>
                <Button id='one' variant="outlined" onClick={() => setactiveTab('favourites')} > Favourites </Button>
            </Stack>
        </div>;
        displ = <Dish />;
    }
    if (activeTab === 'favourites') {
        mainButtons = <div>
            <Stack direction="row" spacing={2}>
                <Button id='one' variant="outlined" onClick={() => setactiveTab('randomDish')} > Random dish </Button>
                <Button id='one' variant="outlined" onClick={() => setactiveTab('favourites')} > Favourites </Button>
                <Button id='one' variant="outlined" onClick={showAddCustomDish} > Add custom dish </Button>
            </Stack>
        </div>;
        displ =
            <div>
                <Favourites />
                <Backdrop
                    sx={{ color: '#fff', zIndex: (theme) => theme.zIndex.drawer + 1 }}
                    open={open}
                >
                    <div className='closeAddDish'>
                        <IconButton color="error">
                            <CloseIcon
                                fontSize="large"
                                onClick={closeAddCustomDish}
                            />
                        </IconButton>
                        <AddCustomDish />
                        <Stack margin={'20px'}>
                            <Button className='addCustomDish' variant="contained" onClick={addDish} > Add Custom Dish </Button>
                        </Stack>
                    </div>
                </Backdrop >;
            </div >
    }



    // const addDish = () => {
    //     dispatch({
    //         type: 'ADD_DISH',
    //         payload: {
    //             data: [{
    //                 strMeal: strMeal.value,
    //                 strInstructions: strInstructions.value,
    //                 strMealThumb: 'https://res.cloudinary.com/crunchbase-production/image/upload/c_lpad,f_auto,q_auto:eco,dpr_1/v1479829522/bxpgav8mvdsibvxctd1d.jpg'
    //             }]
    //         }
    //     })
    //     setTitle('');
    //     setDescription('');
    // }

    return (
        <div className='container'>
            <div className="topBar">
                {mainButtons}
            </div >
            <div>
                {displ}
            </div>
        </div>
    )
}


export default TopBar;
